import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getMealById } from "../api/meals";
import { Meal } from "../types/meal";
import { useSelection } from "../context/SelectionContext";

export default function DetailView() {
  const { id } = useParams<{ id: string }>();
  const [meal, setMeal] = useState<Meal | null>(null);
  const { list } = useSelection();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) getMealById(id).then(setMeal);
  }, [id]);

  if (!meal) return <p>Loading...</p>;

  const idx = list.findIndex(m => m.idMeal === id);

  return (
    <div>
      <h2>{meal.strMeal}</h2>
      <img src={meal.strMealThumb || ""} alt={meal.strMeal} style={{ maxWidth: "300px" }} />
      <p><b>Category:</b> {meal.strCategory}</p>
      <p><b>Area:</b> {meal.strArea}</p>
      <p>{meal.strInstructions?.slice(0,200)}...</p>

      <div>
        <button disabled={idx <= 0} onClick={() => navigate(`/meal/${list[idx-1].idMeal}`)}>Prev</button>
        <button disabled={idx < 0 || idx >= list.length-1} onClick={() => navigate(`/meal/${list[idx+1].idMeal}`)}>Next</button>
      </div>
    </div>
  );
}
